import './App.css';
import React from 'react'
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0
    }
  }

  handleClick = () => {
    this.setState({ count: this.state.count + 1 })
  }
  render() {
    return (
      <div className="app">
        hello
        <button onClick={this.handleClick} > {this.state.count}</button>
      </div>
    );
  }
}





export default App;
